mapper.select:
select order_no, user_id, receiver, dely_addr, user_phone, order_date, payment, order_req , order_date , order_state

from order_list
where user_id=#{userId} and order_no=#{orderNo}

mapper.update:
update order_list
set order_state =#{orderState}
where user_id= #{userId} and order_no=#{orderNo}
